function ChatInterface({ currentSession, sessions, setSessions }) {
  const [userInput, setUserInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [knowledgeBase, setKnowledgeBase] = useState('');
  const [showHistory, setShowHistory] = useState(true);
  const [expandedMessages, setExpandedMessages] = useState({});
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [sessions[currentSession]]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!userInput.trim() || isLoading) return;

    const question = userInput.trim();
    setUserInput('');
    setIsLoading(true);

    // Add user message to session
    const newMessage = {
      q: question,
      a: '',
      timestamp: new Date().toLocaleString(),
      type: 'user'
    };

    setSessions(prev => ({
      ...prev,
      [currentSession]: [...prev[currentSession], newMessage]
    }));

    try {
      // Get AI response from backend
      const response = await chatAPI.sendMessage(
        question, 
        knowledgeBase, 
        sessions[currentSession], 
        currentSession
      );

      if (response.success) {
        // Add AI response to session
        const aiMessage = {
          q: question,
          a: response.response,
          timestamp: response.timestamp,
          type: 'ai',
          tables: response.tables || [],
          plot: response.plot
        };

        setSessions(prev => ({
          ...prev,
          [currentSession]: prev[currentSession].map((msg, index) => 
            index === prev[currentSession].length - 1 ? aiMessage : msg
          )
        }));

      } else {
        toast.error('Failed to get response from AI');
      }

    } catch (error) {
      toast.error('Failed to get response. Please try again.');
      console.error('Error:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleFileUpload = async (files) => {
    try {
      const response = await fileAPI.upload(files);
      if (response.success) {
        setKnowledgeBase(response.knowledge_base);
        toast.success(response.message);
      } else {
        toast.error('Failed to process files');
      }
    } catch (error) {
      console.error('File upload error:', error);
      toast.error('Failed to upload files. Please try again.');
    }
  };

  const toggleMessage = (index) => {
    setExpandedMessages(prev => ({
      ...prev,
      [index]: !prev[index]
    }));
  };

  const exportChatHistory = () => {
    const currentSessionMessages = sessions[currentSession] || [];
    if (currentSessionMessages.length === 0) {
      toast.error('No chat history to export');
      return;
    }

    let chatExport = "";
    currentSessionMessages.forEach((msg, i) => {
      const timestamp = msg.timestamp || 'No timestamp';
      chatExport += `=== Message ${i+1} | ${timestamp} ===\n`;
      chatExport += `User: ${msg.q}\n\n`;
      chatExport += `Assistant: ${msg.a}\n\n`;
      chatExport += "-".repeat(50) + "\n\n";
    });

    const blob = new Blob([chatExport], { type: 'text/plain' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `chat_history_${currentSession}_${new Date().toISOString().slice(0, 19).replace(/:/g, '-')}.txt`;
    a.click();
    window.URL.revokeObjectURL(url);
    toast.success('Chat history exported successfully!');
  };

  const currentSessionMessages = sessions[currentSession] || [];

  return (
    <ChatContainer>
      {/* <FileUploadSection>
        <FileUpload onFileUpload={handleFileUpload} />
      </FileUploadSection> */}

      <ChatSection>
        <MessagesContainer>
          {currentSessionMessages.length === 0 ? (
            <EmptyState>
              <h3>Welcome to TONIC AI! 🤖</h3>
              <p>Start a conversation by asking a question or upload some documents to get started.</p>
            </EmptyState>
          ) : (
            currentSessionMessages.map((message, index) => (
              <ChatMessage key={index} message={message} />
            ))
          )}

          {isLoading && (
            <LoadingContainer>
              <div className="spinner" style={{ width: '32px', height: '32px' }}></div>
              <p>Generating response...</p>
            </LoadingContainer>
          )}

          <div ref={messagesEndRef} />
        </MessagesContainer>

        <InputSection>
          <InputForm onSubmit={handleSubmit}>
            <TextArea
              value={userInput}
              onChange={(e) => setUserInput(e.target.value)}
              placeholder="Ask me anything about your uploaded documents or any general question..."
              disabled={isLoading}
            />
            <SendButton type="submit" disabled={isLoading || !userInput.trim()}>
              {isLoading ? (
                <div className="spinner" style={{ width: '20px', height: '20px' }}></div>
              ) : (
                <Send size={20} />
              )}
              Send
            </SendButton>
          </InputForm>
        </InputSection>
      </ChatSection>

      {/* Chat History Section */}
      {currentSessionMessages.length > 0 && (
        <ChatHistorySection>
          <HistoryHeader>
            <HistoryTitle>
              <History size={20} />
              Chat History: {currentSession}
            </HistoryTitle>
            <HistoryControls>
              <ToggleButton onClick={() => setShowHistory(!showHistory)}>
                {showHistory ? <ChevronUp size={16} /> : <ChevronDown size={16} />}
                {showHistory ? 'Hide' : 'Show'} History
              </ToggleButton>
              <ExportButton onClick={exportChatHistory}>
                <Download size={16} />
                Export
              </ExportButton>
            </HistoryControls>
          </HistoryHeader>

          <HistoryContent isOpen={showHistory}>
            <HistoryMessages>
              {currentSessionMessages.map((message, index) => (
                <HistoryMessage key={index}>
                  <MessageHeader onClick={() => toggleMessage(index)}>
                    <MessageTitle>
                      💬 Message {index + 1}: {message.q.substring(0, 50)}...
                    </MessageTitle>
                    <MessageTimestamp>
                      {message.timestamp}
                      {expandedMessages[index] ? <ChevronUp size={16} /> : <ChevronDown size={16} />}
                    </MessageTimestamp>
                  </MessageHeader>
                  <MessageContent isExpanded={expandedMessages[index]}>
                    <MessageText>
                      <h4>🙋 User:</h4>
                      <p>{message.q}</p>
                    </MessageText>
                    <MessageText>
                      <h4>🤖 Assistant:</h4>
                      <p>{message.a}</p>
                    </MessageText>
                  </MessageContent>
                </HistoryMessage>
              ))}
            </HistoryMessages>
          </HistoryContent>
        </ChatHistorySection>
      )}
    </ChatContainer>
  );
}

