function ChatInterface({ currentSession, sessions, setSessions }) {
  const [userInput, setUserInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [knowledgeBase, setKnowledgeBase] = useState('');
  const [currentResponse, setCurrentResponse] = useState('');
  const [currentPlot, setCurrentPlot] = useState(null);
  const [currentTables, setCurrentTables] = useState([]);
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [sessions[currentSession]]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!userInput.trim() || isLoading) return;

    const question = userInput.trim();
    setUserInput('');
    setIsLoading(true);

    // Add user message to session
    const newMessage = {
      q: question,
      a: '',
      timestamp: new Date().toLocaleString(),
      type: 'user'
    };

    setSessions(prev => ({
      ...prev,
      [currentSession]: [...prev[currentSession], newMessage]
    }));

    try {
      // Get AI response from backend
      const response = await chatAPI.sendMessage(
        question, 
        knowledgeBase, 
        sessions[currentSession], 
        currentSession
      );

      if (response.success) {
        // Add AI response to session
        const aiMessage = {
          q: question,
          a: response.response,
          timestamp: response.timestamp,
          type: 'ai',
          tables: response.tables || [],
          plot: response.plot
        };

        setSessions(prev => ({
          ...prev,
          [currentSession]: prev[currentSession].map((msg, index) => 
            index === prev[currentSession].length - 1 ? aiMessage : msg
          )
        }));

        setCurrentResponse(response.response);
        setCurrentTables(response.tables || []);
        setCurrentPlot(response.plot);

      } else {
        toast.error('Failed to get response from AI');
      }

    } catch (error) {
      toast.error('Failed to get response. Please try again.');
      console.error('Error:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleFileUpload = async (files) => {
    try {
      const response = await fileAPI.upload(files);
      if (response.success) {
        setKnowledgeBase(response.knowledge_base);
        toast.success(response.message);
      } else {
        toast.error('Failed to process files');
      }
    } catch (error) {
      console.error('File upload error:', error);
      toast.error('Failed to upload files. Please try again.');
    }
  };

  const downloadTableAsCSV = (table, index) => {
    const headers = table.headers.join(',');
    const rows = table.data.map(row => row.join(',')).join('\n');
    const csv = `${headers}\n${rows}`;

    const blob = new Blob([csv], { type: 'text/csv' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `table_${index + 1}_${currentSession}.csv`;
    a.click();
    window.URL.revokeObjectURL(url);
  };

  const downloadPlot = () => {
    if (currentPlot && currentPlot.image) {
      const link = document.createElement('a');
      link.href = `data:image/png;base64,${currentPlot.image}`;
      link.download = `plot_${currentSession}_${Date.now()}.png`;
      link.click();
    }
  };

  const currentSessionMessages = sessions[currentSession] || [];

  return (
    <ChatContainer>
      <FileUploadSection>
        <FileUpload onFileUpload={handleFileUpload} />
      </FileUploadSection>

      <ChatSection>
        <ChatHeader>
          <ChatTitle>ðŸ’¬ Ask a Question</ChatTitle>
        </ChatHeader>

        <MessagesContainer>
          {currentSessionMessages.length === 0 ? (
            <EmptyState>
              <h3>Welcome to TONIC AI! ðŸ¤–</h3>
              <p>Start a conversation by asking a question or upload some documents to get started.</p>
            </EmptyState>
          ) : (
            currentSessionMessages.map((message, index) => (
              <ChatMessage key={index} message={message} />
            ))
          )}

          {isLoading && (
            <LoadingContainer>
              <div className="spinner" style={{ width: '32px', height: '32px' }}></div>
              <p>Generating response...</p>
            </LoadingContainer>
          )}

          <div ref={messagesEndRef} />
        </MessagesContainer>

        <InputSection>
          <InputForm onSubmit={handleSubmit}>
            <TextArea
              value={userInput}
              onChange={(e) => setUserInput(e.target.value)}
              placeholder="Ask me anything about your uploaded documents or any general question..."
              disabled={isLoading}
            />
            <SendButton type="submit" disabled={isLoading || !userInput.trim()}>
              {isLoading ? (
                <div className="spinner" style={{ width: '20px', height: '20px' }}></div>
              ) : (
                <Send size={20} />
              )}
              Send
            </SendButton>
          </InputForm>
        </InputSection>
      </ChatSection>

      {currentPlot && (
        <PlotSection>
          <ResponseHeader>
            <BarChart3 size={20} />
            Generated Visualization
          </ResponseHeader>
          <div style={{ textAlign: 'center', padding: '20px' }}>
            {currentPlot.image && (
              <img 
                src={`data:image/png;base64,${currentPlot.image}`} 
                alt="Generated Plot" 
                style={{ maxWidth: '100%', height: 'auto', marginBottom: '15px', borderRadius: '8px' }}
              />
            )}
            <DownloadButton onClick={downloadPlot}>
              <Download size={16} />
              Download Plot
            </DownloadButton>
          </div>
        </PlotSection>
      )}

      {currentTables.length > 0 && (
        <TableSection>
          {currentTables.map((table, index) => (
            <TableContainer key={index}>
              <TableHeader>
                <TableTitle>ðŸ“Š Table {index + 1}</TableTitle>
                <DownloadButton onClick={() => downloadTableAsCSV(table, index)}>
                  <Download size={16} />
                  Download CSV
                </DownloadButton>
              </TableHeader>
              <Table>
                <thead>
                  <tr>
                    {table.headers.map((header, i) => (
                      <Th key={i}>{header}</Th>
                    ))}
                  </tr>
                </thead>
                <tbody>
                  {table.data.map((row, i) => (
                    <tr key={i}>
                      {row.map((cell, j) => (
                        <Td key={j}>{cell}</Td>
                      ))}
                    </tr>
                  ))}
                </tbody>
              </Table>
            </TableContainer>
          ))}
        </TableSection>
      )}
    </ChatContainer>
  );
}

export default ChatInterface;
