import React from 'react';
import ReactMarkdown from 'react-markdown';
import { User, Bot, Clock, Download, BarChart3, Table as TableIcon } from 'lucide-react';
import styled from 'styled-components';

const MessageContainer = styled.div`
  display: flex;
  justify-content: ${props => props.isUser ? 'flex-end' : 'flex-start'};
  margin-bottom: 20px;
  padding: 0 20px;
`;

const MessageWrapper = styled.div`
  max-width: 70%;
  display: flex;
  gap: 12px;
  align-items: flex-start;
  flex-direction: ${props => props.isUser ? 'row-reverse' : 'row'};
`;

const Avatar = styled.div`
  width: 40px;
  height: 40px;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  flex-shrink: 0;
  font-weight: bold;
  color: white;

  ${props => props.type === 'user' && `
    background: linear-gradient(135deg, #FF6B35 0%, #e53e3e 100%);
  `}

  ${props => props.type === 'ai' && `
    background: linear-gradient(135deg, #4a5568 0%, #2d3748 100%);
  `}
`;

const MessageContent = styled.div`
  background: ${props => props.isUser ? 'linear-gradient(135deg, #FF6B35 0%, #e53e3e 100%)' : 'white'};
  color: ${props => props.isUser ? 'white' : '#4a5568'};
  border-radius: 18px;
  padding: 16px 20px;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
  border: 1px solid ${props => props.isUser ? 'transparent' : '#e2e8f0'};
  max-width: 100%;
  word-wrap: break-word;
  overflow-wrap: break-word;
  position: relative;

  ${props => props.isUser && `
    border-bottom-right-radius: 6px;
  `}

  ${props => !props.isUser && `
    border-bottom-left-radius: 6px;
  `}
`;

const MessageHeader = styled.div`
  display: flex;
  align-items: center;
  gap: 8px;
