function ChatMessage({ message }) {
  const isUser = message.type === 'user';

  const downloadTableAsCSV = (table, index) => {
    const headers = table.headers.join(',');
    const rows = table.data.map(row => row.join(',')).join('\n');
    const csv = `${headers}\n${rows}`;

    const blob = new Blob([csv], { type: 'text/csv' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `table_${index + 1}_${Date.now()}.csv`;
    a.click();
    window.URL.revokeObjectURL(url);
  };

  const downloadPlotAsPNG = (plotImage) => {
    const link = document.createElement('a');
    link.href = `data:image/png;base64,${plotImage}`;
    link.download = `plot_${Date.now()}.png`;
    link.click();
  };

  return (
    <MessageContainer isUser={isUser}>
      <MessageWrapper isUser={isUser}>
        <Avatar type={message.type}>
          {isUser ? <User size={20} /> : <Bot size={20} />}
        </Avatar>

        <MessageContent isUser={isUser}>
          <MessageHeader isUser={isUser}>
            <MessageAuthor isUser={isUser}>
              {isUser ? 'You' : 'TONIC AI Assistant'}
            </MessageAuthor>
            <MessageTime isUser={isUser}>
              <Clock size={12} />
              {message.timestamp}
            </MessageTime>
          </MessageHeader>

          <MessageText isUser={isUser}>
            {isUser ? (
              <p>{message.q}</p>
            ) : (
              <>
                <ReactMarkdown>{message.a}</ReactMarkdown>

                {/* Show visualizations if available */}
                {(message.tables && message.tables.length > 0) || message.plot ? (
                  <VisualizationSection>
                    {/* Tables Section */}
                    {message.tables && message.tables.length > 0 && (
                      <>
                        <SectionTitle>
                          <TableIcon size={18} />
                          Extracted Tables ({message.tables.length})
                        </SectionTitle>
                        {message.tables.map((table, index) => (
                          <TableContainer key={index}>
                            <TableHeader>
                              <TableTitle>
                                <TableIcon size={16} />
                                Table {index + 1}
                              </TableTitle>
                              <DownloadButton onClick={() => downloadTableAsCSV(table, index)}>
                                <Download size={14} />
                                Download CSV
                              </DownloadButton>
                            </TableHeader>
                            <Table>
                              <thead>
                                <tr>
                                  {table.headers.map((header, i) => (
                                    <Th key={i}>{header}</Th>
                                  ))}
                                </tr>
                              </thead>
                              <tbody>
                                {table.data.map((row, i) => (
                                  <tr key={i}>
                                    {row.map((cell, j) => (
                                      <Td key={j}>{cell}</Td>
                                    ))}
                                  </tr>
                                ))}
                              </tbody>
                            </Table>
                          </TableContainer>
                        ))}
                      </>
                    )}

                    {/* Plot Section */}
                    {message.plot && (
                      <>
                        <SectionTitle>
                          <BarChart3 size={18} />
                          Generated Visualization
                        </SectionTitle>
                        <PlotContainer>
                          <PlotTitle>
                            <BarChart3 size={16} />
                            Data Visualization
                          </PlotTitle>
                          {message.plot.image ? (
                            <>
                              <PlotImage 
                                src={`data:image/png;base64,${message.plot.image}`} 
                                alt="Generated Plot"
                              />
                              <PlotDownloadButton onClick={() => downloadPlotAsPNG(message.plot.image)}>
                                <Download size={16} />
                                Download Plot as PNG
                              </PlotDownloadButton>
                            </>
                          ) : (
                            <>
                              <EmptyPlot>
                                Chart visualization would be displayed here
                              </EmptyPlot>
                              <NoDataMessage>
                                Plot code was generated but image could not be rendered
                              </NoDataMessage>
                            </>
                          )}
                        </PlotContainer>
                      </>
                    )}
                  </VisualizationSection>
                ) : null}
              </>
            )}
          </MessageText>
        </MessageContent>
      </MessageWrapper>
    </MessageContainer>
  );
}

